import NavbarComponent from "./NavbarComponent";

const LoginComponent = () =>{
    const [state,setState] = useState({
        username:"",
        password:""
    })

    const {username,password} = state

    const inputValue = name => event =>{
        setState({...state,[name]:event.target.value})
    }

    const submitForm =(e)=>{
        e.preventDefault();
        console.log("API URL",process.env.REACT_APP_API)
        axios
        .post("http://localhost:5500/api/blogs/create",{title,content,author})
        .then(response => {
            Swal.fire('บันทึกข้อมูลเรียบร้อย','ข้อมูลถูกบันทึกแล้ว','success')
            setState({...state,title:"",author:""})
            setContent("")
        })
        .catch(err=>{
            alert(err.response.data.error);
        })
    }

    return(
        <div className="container p-5">
            <NavbarComponent/>
            <h1>เข้าสู่ระบบ | Admin</h1>
            <form onSubmit={submitForm}>
                <div className="form-group">
                    <label>Username</label>
                    <input type="text" className="form-control" value={username} onChange={inputValue("title")}/>
                </div>
                
                <div className="form-group">
                    <label>Password</label>
                    <input type="text" className="form-control" value={password} onChange={inputValue("author")}/>
                </div>
                <br/>
                <input type="submit" value="บันทึก" className="btn btn-primary"/>
            </form>
        </div>
    )
}
export default LoginComponent;
